mapper.select:
select 

from redList
where adminNo = #{adminNo,jdbcType=INTEGER}
  and workNo = #{workNo,jdbcType=INTEGER}

mapper.delete:
delete from redList
where adminNo = #{adminNo,jdbcType=INTEGER}
  and workNo = #{workNo,jdbcType=INTEGER}

mapper.update:
update redList

  
    reason = #{reason,jdbcType=VARCHAR},
  
  
    created = #{created,jdbcType=DATE},
  

where adminNo = #{adminNo,jdbcType=INTEGER}
  and workNo = #{workNo,jdbcType=INTEGER}

update redList
set reason = #{reason,jdbcType=VARCHAR},
  created = #{created,jdbcType=DATE}
where adminNo = #{adminNo,jdbcType=INTEGER}
  and workNo = #{workNo,jdbcType=INTEGER}
